import styled from 'styled-components';

const BigButton = styled.buttom`
  font-size: 3rem;
  background: none;
  border: 0;
  &:hover {
    color: var(--red);
    cursor: pointer;
  }
`;

export default function RemoveFromCart({ id }) {
  return (
    <BigButton type='button' title='Remove This Item From Cart'>
      &times;
    </BigButton>
  );
}
