import { CardElement, Elements, useStripe } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { useState } from 'react';
import SickButton from './styles/SickButton';

const { default: styled } = require('styled-components');

const CheckoutFormStyles = styled.form`
  box-shadow: 0 1px 2px 2px rgba(0, 0, 0, 0.4);
  border: 1px solid rgba(0, 0, 0, 0.06);
  border-radius: 5px;
  padding: 1rem;
  display: grid;
  grid-gap: 1rem;
`;

const stripeLib = loadStripe(process.env.NEXT_PUBLIC_STRIPE_KEY);

function Checkout() {
  const [error, setError] = useState();
  const [loading, setLoading] = useState(false);
  const stripe = useStripe();
  function handleSubmit(e) {
    // 1. Stop the form from submitting and turn the loader on
    e.preventDefault();
    console.log('Gotta work on that');
    // 2 start the page transition
    // 3 cereate payment method via stripe, token created if succesful
    // 4 handle any errors from stripe
    // 5 send token from step 3 to our keystone, via custom mutation!
    // 6 change the page to view order
    // 7 close the cart
    // 8 turn the loader off
  }

  return (
    <Elements stripe={stripeLib}>
      <CheckoutFormStyles onSubmit={handleSubmit}>
        <CardElement />
        <SickButton>Pay now</SickButton>
      </CheckoutFormStyles>
    </Elements>
  );
}

export { Checkout };
